import { createSelector, createFeatureSelector } from '@ngrx/store';
export const selectAntiHeroState = createFeatureSelector('antiHeroState');
export const selectAntiHeroes = () => createSelector(selectAntiHeroState, state => state.antiHeroes);
export const selectAntiHero = id => createSelector(selectAntiHeroState, state => state.antiHeroes.find(d => d.id === id));
